					clearButton = ui.textClearButtonElement;

				self._unbindEvents();

				if (textLine) {
					textLine.parentElement.removeChild(ui.textLineElement);
				}

				if (clearButton) {
					clearButton.parentElement.removeChild(ui.textClearButtonElement);
				}

